mapper.insert:

insert into weshop_visit_journal (weshop_visit_id, visit_token, visit_user_id,
  goods_id, visit_time, store_id, 
  comp_id, created_at, updated_at, 
  created_person_id, updated_person_id, is_deleted,uid,uid_type
  )
values (#{weshopVisitId,jdbcType=BIGINT}, #{visitToken,jdbcType=VARCHAR}, #{visitUserId,jdbcType=BIGINT}, 
  #{goodsId,jdbcType=INTEGER}, #{visitTime,jdbcType=BIGINT}, #{storeId,jdbcType=BIGINT}, 
  #{compId,jdbcType=INTEGER}, #{createdAt,jdbcType=BIGINT}, #{updatedAt,jdbcType=BIGINT}, 
  #{createdPersonId,jdbcType=INTEGER}, #{updatedPersonId,jdbcType=INTEGER}, #{isDeleted,jdbcType=BIT},#{uid},#{uid_type}
  )

mapper.select:

select
count(1)
from weshop_visit_journal
where comp_id = #{compId,jdbcType=BIGINT}
  and store_id = #{storeId,jdbcType=BIGINT}


  and visit_time >= #{visitTimeFloor,jdbcType=BIGINT}




  and visit_time <=  #{visitTimeUpper,jdbcType=BIGINT}

select count(0) from (
  select
  visit_user_id,
  store_id
  from weshop_visit_journal
  where comp_id = #{compId,jdbcType=BIGINT}
    and store_id = #{storeId,jdbcType=BIGINT}


      and visit_time >= #{visitTimeFloor,jdbcType=BIGINT}




      and visit_time <= #{visitTimeUpper,jdbcType=BIGINT}


  group by uid,store_id
) t
